package codeio;

public class ClassAndObject {
	
		String name;
		int age;
		
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Creating Objects 

		ClassAndObject obj = new ClassAndObject();
		obj.name = "Mohammed Suhail";
		obj.age = 20; 
		System.out.println("Name: " + obj.name + "\n" + "Age: " + obj.age);
		
		ClassAndObject obj2 = new ClassAndObject();
		obj2.name = "Mohammed Fayas";
		obj2.age = 19; 
		System.out.println("Name: " + obj2.name + "\n" + "Age: " + obj2.age);
		
	}

}
