package codeio;

public class Inheritance {

	public void Job() {
		System.out.println("Job: IT Job");
	}
	
	public void Salary() {
		System.out.println("Salary: 15,000");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Inheritance 
		
		Inheritance obj = new Inheritance();
		obj.Job();
		obj.Salary(); 
		
	}

}
